import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap";
import MovieList from "./MovieList";
import Filtre from './Filtre';

const App = () => {
  const [movies, setMovies] = useState([
    {
      title: "Inception",
      description: "A mind-bending thriller.",
      posterURL: "https://image-url.com/inception.jpg",
      note: 9,
      videoURL: "https://www.youtube.com/embed/YoHD9XEInc0",
    },
    {
      title: "The Dark Knight",
      description: "A superhero masterpiece.",
      posterURL: "https://image-url.com/darkknight.jpg",
      note: 10,
      videoURL: "https://www.youtube.com/embed/EXeTwQWrcwY",
    },
  ]);

  const [searchTerm, setSearchTerm] = useState("");
  const [Note, setNote] = useState(0);
  const [showVideoModal, setShowVideoModal] = useState(false);
  const [currentVideoURL, setCurrentVideoURL] = useState("");

  const handleMoreDetails = (videoURL) => {
    setCurrentVideoURL(videoURL);
    setShowVideoModal(true);
  };

  const filteredMovies = movies.filter(
    (movie) =>
      (!searchTerm || movie.title.toLowerCase().includes(searchTerm.toLowerCase())) &&
      (!Note || movie.note === Note)
  );

  return (
    <div>
      <h1 style={{ textAlign: "center" }}>Movie App</h1>

      <Filtre setSearchTerm={setSearchTerm} setNote={setNote} />

      <MovieList movies={filteredMovies.length > 0 || searchTerm || Note ? filteredMovies : movies} onMoreDetails={handleMoreDetails} />

      {/* Video Modal */}
      <Modal show={showVideoModal} onHide={() => setShowVideoModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Movie Trailer</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <iframe
            width="100%"
            height="400"
            src={currentVideoURL}
            title="Movie Trailer"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowVideoModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default App;
