import React, { useState } from 'react';
import MovieList from './MovieList';
import Filtre from './Filtre';

const App = () => {
  const [movies, setMovies] = useState([
    {
      title: 'Inception',
      description: 'A mind-bending thriller.',
      posterURL: 'https://image-url.com/inception.jpg',
      note: 9,
    },
    {
      title: 'The Dark Knight',
      description: 'A superhero masterpiece.',
      posterURL: 'https://image-url.com/darkknight.jpg',
      note: 10,
    },
    {
      title: 'The Dark Knight',
      description: 'A superhero masterpiece.',
      posterURL: 'https://image-url.com/darkknight.jpg',
      note: 10,
    },
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [minNote, setMinNote] = useState(0);

  const filteredMovies = movies.filter(
    (movie) =>
      movie.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
      movie.note >= minNote
  );

  const addMovie = () => {
    setMovies([
      ...movies,
      {
        title: 'New Movie',
        description: 'Description here',
        posterURL: 'https://image-url.com/newmovie.jpg',
        note: 7,
      },
    ]);
  };

  return (
    <div>
      <h1 style={{ textAlign: 'center' }}>Movie App</h1>
      <Filter setSearchTerm={setSearchTerm} setMinNote={setMinNote} />
      <button onClick={addMovie} style={{ margin: '20px' }}>
        Add New Movie
      </button>
      <MovieList movies={filteredMovies} />
    </div>
  );
};

export default App;
