import React from 'react';
import { useParams, Link } from 'react-router-dom';

const MovieDetails = ({ movies }) => {
  const { id } = useParams();
  const movie = movies.find((movie) => movie.id === parseInt(id));

  if (!movie) {
    return <p>Film non trouvé</p>;
  }
  return (
    <div style={{ textAlign: 'center', color: 'Black' }}>
      <h2>{movie.title}</h2>
      <img src={movie.posterURL} alt={movie.title} style={{ maxWidth: '300px', margin: '20px auto' }} />
      <p style={{ maxWidth: '600px', margin: '20px auto' }}>{movie.description}</p>
      <p><strong>Note :</strong> {movie.note}/10</p>
      <iframe
          width="100%"
          height="315"
          src={movie.videoURL}
          title={movie.title}
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
        <br />
      <Link to="/" style={{ color: 'mediumpurple', textDecoration: 'none', marginTop: '20px' }}>
        Retour à la liste
      </Link>
    </div>
  );
};

export default MovieDetails;
