import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import MovieList from "./MovieList";
import Filtre from './Filtre';

const App = () => {
  const [movies, setMovies] = useState([
    {
      title: "Inception",
      description: "A mind-bending thriller.",
      posterURL: "https://image-url.com/inception.jpg",
      note: 9,
      videoURL: "https://www.youtube.com/embed/YoHD9XEInc0",
    },
    {
      title: "The Dark Knight",
      description: "A superhero masterpiece.",
      posterURL: "https://image-url.com/darkknight.jpg",
      note: 10,
      videoURL: "https://www.youtube.com/embed/YoHD9XEInc0",
    },
    {
      title: "Interstellar",
      description: "A journey beyond the stars.",
      posterURL: "https://image-url.com/interstellar.jpg",
      note: 9.5,
      videoURL: "https://www.youtube.com/embed/YoHD9XEInc0",
    },
  ]);

  const [showModal, setShowModal] = useState(false);
  const [newMovie, setNewMovie] = useState({
    title: "",
    description: "",
    posterURL: "",
    note: "",
  });

  const [searchTerm, setSearchTerm] = useState("");
  const [Note, setNote] = useState(0);
  const [showVideoModal, setShowVideoModal] = useState(false);
  const [currentVideoURL, setCurrentVideoURL] = useState("");

  const handleMoreDetails = (videoURL) => {
    setCurrentVideoURL(videoURL);
    setShowVideoModal(true);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewMovie({ ...newMovie, [name]: value });
  };

  const handleAddMovie = () => {
    setMovies([...movies, { ...newMovie, note: Number(newMovie.note) }]);
    setShowModal(false);
    setNewMovie({ title: "", description: "", posterURL: "", note: "" });
  };

  const filteredMovies = movies.filter(
    (movie) =>
      (!searchTerm || movie.title.toLowerCase().includes(searchTerm.toLowerCase())) &&
      (!Note || movie.note === Note)
  );

  return (
    <div>
      <h1 style={{ textAlign: 'center' , color:"mediumorchid" }}>RealMovies</h1>
      <p style={{ 
          textAlign: 'center', 
          fontSize: '18px', 
          fontWeight: '500', 
          margin: '20px auto', 
          maxWidth: '600px', 
          lineHeight: '1.5', 
          color: '#333' 
}}>
  Bienvenue dans mon site Web! <br />
  Vous trouverez quelques-uns de mes FavMovies. <br />
  Vous pouvez  ajouter vos propres films préférés. Merci!
</p>

  {/* Filter Component */}
  <div style={{  marginLeft: '70%' }}>
    <Filtre setSearchTerm={setSearchTerm} setNote={setNote} />
  </div>

      {/* Add Movie Button */}
      <Button
          variant="primary"
          onClick={() => setShowModal(true)}
          style={{
            margin: "20px auto",
            display: "block",
            position: "fixed",
            bottom: "20px",
            right: "20px",
          }}
        >
          Add New Movie
    </Button>


      {/* ajouter un film */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add New Movie</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formMovieTitle">
              <Form.Label>Titre</Form.Label>
              <Form.Control
                type="text"
                placeholder="Donnez un titre"
                name="title"
                value={newMovie.title}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group controlId="formMovieDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Entrez une description"
                name="description"
                value={newMovie.description}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group controlId="formPosterURL">
              <Form.Label>Poster URL</Form.Label>
              <Form.Control
                type="text"
                placeholder="Entrez l'URL"
                name="posterURL"
                value={newMovie.posterURL}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group controlId="formMovieRating">
              <Form.Label>Note</Form.Label>
              <Form.Control
                type="number"
                placeholder="Entrez une note"
                name="note"
                value={newMovie.note}
                onChange={handleInputChange}
                min="0"
                max="10"
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={handleAddMovie}>
            Add un film
          </Button>
        </Modal.Footer>
      </Modal>


      <MovieList movies={filteredMovies.length > 0 || searchTerm || Note ? filteredMovies : movies} />
      <Modal show={showVideoModal} onHide={() => setShowVideoModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Movie Trailer</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <iframe
            width="100%"
            height="400"
            src={currentVideoURL}
            title="Movie Trailer"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowVideoModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};


export default App;
