import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import MovieList from './MovieList';

const App = () => {
  const [movies, setMovies] = useState([
    {
      title: 'Inception',
      description: 'A mind-bending thriller.',
      posterURL: 'https://image-url.com/inception.jpg',
      note: 9,
    },
    {
      title: 'The Dark Knight',
      description: 'A superhero masterpiece.',
      posterURL: 'https://image-url.com/darkknight.jpg',
      note: 10,
    },
    {
      title: 'Interstellar',
      description: 'A journey beyond the stars.',
      posterURL: 'https://image-url.com/interstellar.jpg',
      note: 9.5,
    },
  ]);

  const [showModal, setShowModal] = useState(false);
  const [newMovie, setNewMovie] = useState({
    title: '',
    description: '',
    posterURL: '',
    note: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewMovie({ ...newMovie, [name]: value });
  };

  const handleAddMovie = () => {
    setMovies([...movies, { ...newMovie, note: Number(newMovie.note) }]);
    setShowModal(false);
    setNewMovie({ title: '', description: '', posterURL: '', note: '' });
  };

  return (
    <div>
      <h1 style={{ textAlign: 'center' }}>Movie App</h1>

      <Button variant="primary" onClick={() => setShowModal(true)} style={{ margin: '20px' }}>
        Add New Movie
      </Button>

      {/* Modal to Add New Movie */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add New Movie</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formMovieTitle">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter movie title"
                name="title"
                value={newMovie.title}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group controlId="formMovieDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Enter movie description"
                name="description"
                value={newMovie.description}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group controlId="formPosterURL">
              <Form.Label>Poster URL</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter poster URL"
                name="posterURL"
                value={newMovie.posterURL}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group controlId="formMovieRating">
              <Form.Label>Note</Form.Label>
              <Form.Control
                type="number"
                placeholder="Enter note"
                name="note"
                value={newMovie.note}
                onChange={handleInputChange}
                min="0"
                max="10"
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={handleAddMovie}>
            Add Movie
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Movie List */}
      <MovieList movies={movies} />
    </div>
  );
};

export default App;
